CustomField.formula:
if (OR(RecordType_Name__c = 'E&D - Projects', RecordType_Name__c = 'E&D - Projects - Proposal', RecordType_Name__c = 'E&D - Projects - Read Only'),
  if (Amount > 1000000, 'Rock',
    if (Amount <10000, 'Pebble', 'Stone')
  ),
  if (Amount > 500000, 'Rock',
    if (Amount <5000, 'Pebble', 'Stone')
  )
)
